package numericpattern;

public class NumP1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=4;i++){
			
			for(int j=1;j<=i;j++){
				System.out.print(j);
			}
			System.out.println();
			
		}
		
		System.out.println("+++++++++++++++++++++++");
             for(int i=1;i<=4;i++){
			
		        	for(int j=1;j<=i;j++){
				     System.out.print(i);
			        }
			System.out.println();
			
		}

	}

}
